const lead = $input.first().json;

// Email Templates f√ºr verschiedene Tiers
const templates = {
  'üî• HOT': {
    subject: 'Schnelle Frage zu {{company}} - AI Automation',
    body: `Hallo {{name}},\n\nich habe gesehen, dass Sie als {{title}} bei {{company}} die operative Effizienz vorantreiben.\n\nWir haben gerade eine Automation f√ºr ein Tech-Unternehmen √§hnlicher Gr√∂√üe gebaut:\n‚Ä¢ 40% Zeitersparnis im Sales-Prozess\n‚Ä¢ Automatische Lead-Qualifizierung\n‚Ä¢ CRM-Integration ohne manuelle Doppelarbeit\n\nPasst das zu Ihren aktuellen Priorit√§ten?\n\nIch h√§tte gerne 15 Minuten mit Ihnen √ºber Ihre gr√∂√üten Zeitfresser gesprochen.\n\nBeste Gr√º√üe,\n[Your Name]`
  },
  '‚ö° WARM': {
    subject: 'AI-Automation f√ºr {{company}} - Wie skalieren Sie aktuell?',
    body: `Hallo {{name}},\n\nIhr Background bei {{company}} ist interessant - besonders im {{industry}}-Bereich.\n\nWir spezialisieren uns auf AI-Automation f√ºr wachsende Teams. Eine Frage:\nWelche wiederkehrenden Tasks kosten Ihr Team aktuell die meiste Zeit?\n\nIch w√ºrde gerne h√∂ren, wo Sie Reibungsverluste sehen - vielleicht haben wir daf√ºr bereits eine L√∂sung.\n\nBeste Gr√º√üe,\n[Your Name]`
  },
  'üßä COLD': {
    subject: 'Spannende Entwicklung bei {{company}}',
    body: `Hallo {{name}},\n\nich folge {{company}} schon l√§nger und finde die Entwicklung im {{industry}}-Bereich spannend.\n\nWir helfen Unternehmen dieser Gr√∂√üe, durch Automation schneller zu wachsen - ohne zus√§tzliche Headcounts.\n\nGibt es bei Ihnen aktuell Prozesse, die Sie gerne automatisieren w√ºrden?\n\nBeste Gr√º√üe,\n[Your Name]`
  }
};

const tier = lead.properties.Tier?.select?.name || 'üßä COLD';
const template = templates[tier];

// Replace placeholders
const name = lead.properties.Name?.title?.[0]?.plain_text || 'there';
const company = lead.properties.Firma?.rich_text?.[0]?.plain_text || 'Ihrem Unternehmen';
const title = lead.properties.Titel?.rich_text?.[0]?.plain_text || 'Manager';

const emailSubject = template.subject
  .replace('{{company}}', company)
  .replace('{{name}}', name);

const emailBody = template.body
  .replace(/{{name}}/g, name.split(' ')[0])
  .replace(/{{company}}/g, company)
  .replace(/{{title}}/g, title)
  .replace(/{{industry}}/g, 'Tech');

return [{
  json: {
    leadId: lead.id,
    name,
    company,
    email: lead.properties.Email?.email || '',
    subject: emailSubject,
    body: emailBody,
    tier
  }
}];

